function Main(prop) {
  const [meme, setMeme] = React.useState( {
    topText: '',
    bottomText: '',
    randomImage: 'http://i.imgflip.com/1bij.jpg'
  })


  const [allMemeImages, setallMemeImages] = React.useState(memesData)
   let randomMeme = function getRandomMeme(e) {
    let randomNumb = Math.floor(Math.random() * allMemeImages.data.memes.length);
    e.preventDefault() 
    const url = allMemeImages.data.memes[randomNumb].url
    setMeme(prevObj => {
      return {
        ...prevObj,
        randomImage: url
      }
    })
   
   }

